function ip = showim(im, map)% SHOWIM	displays an image somewhat sensibly.%	SHOWIM(IM) shows an image actual size in a no-frills window.%	SHOWIM(IM, MAP) shows the image with the colormap MAP.iptsetpref('ImshowBorder', 'tight');iptsetpref('ImshowAxesVisible', 'off');if nargin == 1	ip = imshow(im, [mmin(im) mmax(im)], 'truesize');elseif nargin == 2	ip = imshow(im, map,'truesize');else	error('imshow: must have an image or an image and map as input args.');end