function uc = unitcirc(n);% UNITCIRC% A = unitcirc(n) returns n coordinates around the unit circle% make a circle for ref% make quadrant oneq = round(n./4);uc = zeros(q, 2);uc(1:q, 1) = linspace(0,1,q)';uc(1:q, 2) = sqrt(1- uc(1:q, 1).^2);uc = repmat(uc, 4, 1);% quad. twouc(q+1:2*q, 1) = -1 * uc(q+1:2*q, 1);uc(q+1:2*q, :) = fliplr(uc(q+1:2*q, :));% quad. threeuc(2*q+1:3*q, :) = -1 * uc(2*q+1:3*q, :);% quad. fouruc(3*q+1:4*q, 2) = -1 * uc(3*q+1:4*q, 2);uc(3*q+1:4*q, :) = fliplr(uc(3*q+1:4*q, :));